tinymce.PluginManager.add('pinecode', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('pinecode', {
        text: '<>',
        type: 'menubutton',
        icon: false,
        menu: [
                {text: 'PHP', onclick: function() {editor.insertContent('<pre class="line-numbers"><code class=" language-php">Insert PHP Code Here</code></pre>');}},
                {text: 'JS', onclick: function() {editor.insertContent('<pre class="line-numbers"><code class=" language-javascript">Insert JavaScript Code Here</code></pre>');}},
                {text: 'MK', onclick: function() {editor.insertContent('<pre class="line-numbers"><code class=" language-markup">Insert Markup Code Here</code></pre>');}},
            ]
    });
});